	<script type="text/javascript">
   
        $('#nicks').blur(function(){


           $len= $(this).val().length;

           $nick = $(this).val();

           if($len < 6)
           {
              $('#nicklen').css('color','red').text('* 用户名长度为六个字符以上');
              $('#nick').text('未完成');
                          //先移除未完成图标
                    $('#nickico').removeClass('icon_succM');
                          //添加属性
                     $('#nickico').addClass('icon_errorM');
                     one = false;
           }else{

            $('#nicklen').text('');
            one = true;
           }
if(one){
            $.post('/home/dofullinfo',{nick:$nick,'_token':'{{csrf_token()}}'},function(data){

                  if(data == 0)
                  {
                            $('#nick').text('已完成');
                          //先移除未完成图标
                            $('#nickico').removeClass('icon_errorM');
                          //添加属性
                            $('#nickico').addClass('icon_succM');
                    }else{

                       $('#nicklen').css('color','red').text('* 用户名已被注册');
                       $('#nick').text('未完成');
                          //先移除未完成图标
                            $('#nickico').removeClass('icon_succM');
                          //添加属性
                            $('#nickico').addClass('icon_errorM');
                    }


             },'json')
}
    
        })


